package fundamentals;

public class LogicalOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10, b=20;
//		System.out.println(true&&false);
//		System.out.println(true||false);
		
		System.out.println(a>10 && b>10);
		System.out.println(a>10 || b>10);
		
//		System.out.println(!true);
		
		System.out.println(!(a>b));
		
		// && operator work and returns true only when both condition are true.
		// || operator work and returns true only when one condition is true.
       //  ! operator work and returns opposite of conditions means true mean false 
	  //      and false mean true .
		
		}

}
